package net.ligreto.exceptions;

public class LigretoException extends Exception {

	/**
	 * Auto-generated stuff.
	 */
	private static final long serialVersionUID = 1L;

	public LigretoException() {
	}

	public LigretoException(String message) {
		super(message);
	}

	public LigretoException(Throwable cause) {
		super(cause);
	}

	public LigretoException(String message, Throwable cause) {
		super(message, cause);
	}

}
